package com.javaconcepts.two.pointer;

public class L392InSubsequence {

    public boolean isSubsequence(String s, String t) {
        int sp = 0;
        int tp = 0;

        while (sp < s.length() && tp < t.length()) {
            if (s.charAt(sp) == t.charAt(tp)) {
                sp++;
            }
            tp++;
        }

        return sp == s.length();
    }

    public static void main(String[] args) {
        String s = "leeeeetcode";

        L392InSubsequence l392InSubsequence = new L392InSubsequence();
        System.out.println(l392InSubsequence.isSubsequence(s, "\"\""));
        ;
    }
}
